import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';

import {View, Text } from 'react-native'
import * as firebase from 'firebase'

const firebaseConfig = {
  apiKey: "AIzaSyCpJz9TuWZJTy_RxGKnVASxBHygux7JhsM",
  authDomain: "instagram-dev-72488.firebaseapp.com",
  projectId: "instagram-dev-72488",
  storageBucket: "instagram-dev-72488.appspot.com",
  messagingSenderId: "1033749248643",
  appId: "1:1033749248643:web:443b143b1969eb255179b6",
  measurementId: "G-KYK2JT0P80"
};

if(firebase.apps.length === 0){
  firebase.initializeApp(firebaseConfig)
}
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

import LandingScreen from './components/auth/Landing'
import RegisterScreen from './components/auth/Register'

const Stack = createStackNavigator();



export class App extends Component {
  constructor(props){
    super(props);
    this.state={
      loaded:false,
    }
  }
  componentDidMount(){
    firebase.auth().onAuthStateChanged((user) => {
      if(!user){
        this.setState({
          loggedIn: false,
          loaded:true,
        })
      }else{
        this.setState({
          loggedIn: true,
          loaded:true,
        })
      }
    })
  }
  render() {
    const { loggedIn, loaded} = this.state
    if(!loaded){
      return(
        <View style = {{flex: 1, justifyContent: 'center' }}>
          <Text>Loading</Text>
        </View>
      )
    }
    return (
      <NavigationContainer>
        <Stack.Navigator initialRouteName="Landing">
          <Stack.Screen name="Landing" component={LandingScreen} options={{headerShown: false}}/>
          <Stack.Screen name="Register" component={RegisterScreen}/>

        </Stack.Navigator>
       </NavigationContainer>

    );
  }
}

export default App
